// Check if we have hit a basement floor already or not
bool isNeg = false;

// For each parenthese, increase or decrease the current floor
for (int i = 0; i < input.Length; i++)
{
    if (input[i].Equals('('))
    {
        floor++;
    }
    else if(input[i].Equals(')'))
    {
        floor--;
    }

    if(floor == -1 && !isNeg)
    {
        Console.WriteLine(i+1);
        isNeg = true;
    }
}

// Final floor printed out
Console.WriteLine(floor);